	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(RecetaPeer::DATABASE_NAME);

		if ($this->isColumnModified(RecetaPeer::REC_ID)) $criteria->add(RecetaPeer::REC_ID, $this->rec_id);
		if ($this->isColumnModified(RecetaPeer::REC_NOMBRE_RECETA)) $criteria->add(RecetaPeer::REC_NOMBRE_RECETA, $this->rec_nombre_receta);
		if ($this->isColumnModified(RecetaPeer::REC_INGREDIENTES)) $criteria->add(RecetaPeer::REC_INGREDIENTES, $this->rec_ingredientes);
		if ($this->isColumnModified(RecetaPeer::REC_INSTRUCCIONES)) $criteria->add(RecetaPeer::REC_INSTRUCCIONES, $this->rec_instrucciones);
		if ($this->isColumnModified(RecetaPeer::REC_VINO)) $criteria->add(RecetaPeer::REC_VINO, $this->rec_vino);
		if ($this->isColumnModified(RecetaPeer::REC_NOMBRE_BLOGGER)) $criteria->add(RecetaPeer::REC_NOMBRE_BLOGGER, $this->rec_nombre_blogger);
		if ($this->isColumnModified(RecetaPeer::REC_EMAIL_BLOGGER)) $criteria->add(RecetaPeer::REC_EMAIL_BLOGGER, $this->rec_email_blogger);
		if ($this->isColumnModified(RecetaPeer::REC_URL_BLOGGER)) $criteria->add(RecetaPeer::REC_URL_BLOGGER, $this->rec_url_blogger);
